import React, { useState, useEffect } from "react"
import { useParams, useLocation } from "react-router-dom";
import img from "../images/sphinx-bucegi-romania.jpg"


export default function ObiectivC(props) {

    const location = useLocation();
    const [loading, setLoading] = useState(true);
    const [data, setData] = useState(null);
    const [id, setId] = useState(null);

    useEffect(() => {
        // Simulăm o întârziere pentru a demonstra încărcarea datelor
        // const delay = setTimeout(() => {
        //     const fetchedData = location.state && location.state.data && location.state.data[0];
        //     const fetchedId = location.state.data.id
        //     setData(fetchedData);
        //     setId(fetchedId)
        //     setLoading(false);
        // }, 1000);
            const fetchedData =location.state.data[0];
            const fetchedId = location.state && location.state.data && location.state.data.id
            setData(fetchedData)
            setId(fetchedId)
                // setLoading(false)
            
            
            

        return () => {
            // clearTimeout(delay);
        };
    }, [location.state]);

    useEffect(() => {

        setLoading(data !== null? false:true)
        console.log("Data", data)

    }, [data])

    var imgs;
    try {
        imgs = require("../images/" + id + ".jpg")

    }
    catch (err) {
        imgs = img;
    }

    if (loading) {
        // return <div className="loading">Loading...</div>;
        return <div className="lds-ring"><div></div><div></div><div></div><div></div></div>
    }

    if (!data) {
        return <div>No data available.</div>;
    }

    console.log(data);
    console.log(id);


    return (

        <div className="obiectivC">

            <div className="obiectivC_content">

                <h2>{data.nume}</h2>
                <p>{data.descriere}</p>

            </div>

            <div className="obiectivC_img">
                <img src={imgs} alt="img" />
            </div>

        </div>
    )
}
