import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";
import img from "../images/sphinx-bucegi-romania.jpg"
export default function Card(props) {

    var imgs;
    try {
        imgs = require("../images/" + props.item.id + ".jpg")

    }
    catch (err) {
        imgs = img;
    }

    const [dbList, setdbList] = useState([]);


    // useEffect(() => {
    //     getFromDB(); // Apelul inițial pentru a obține datele de la server
    //   }, []);


    // const getFromDB = () => {
    //     Axios.post("localhost:3001/tipobiectiv",{id:props.key}).then((response) =>{
    //         setdbList(response.data)
    //     });
    // };


    const handleCardClick = (id) => {
        Axios.post("http://localhost:3001/tipobiectiv", { id: id })
            .then((response) => {
                // Dacă cererea a fost realizată cu succes, primești datele cardului în response.data
                setdbList(response.data);

                // Aici poți seta datele cardului într-un state al componente sau le poți trimite către o altă componentă pentru afișare
                <Link to="obiectivC"></Link>
               
            })
            .catch((error) => {
                // În caz de eroare, afișează eroarea în consolă
                console.log("Eroare la obținerea datelor cardului:", error);
            });
    };




    return (
        <div className="card" onClick={handleCardClick(props.item.id)}>

            <div className="img">

                <img src={imgs} alt="img" />

            </div>
            <div className="description">
                <h3>{props.item.nume}</h3>
                <p>{props.item.descriere}</p>

            </div>
        </div>
    )
}
