import React, { useState } from "react";
import arrowLeft from "../images/arrow_back_FILL0_wght400_GRAD0_opsz48.svg";
import arrowRight from "../images/arrow_forward_FILL0_wght400_GRAD0_opsz48.svg";


const SlideShow = ({ images }) => {
    const [currentIndex, setCurrentIndex] = useState(0);
    
    const nextSlide = () => {
      setCurrentIndex(currentIndex === images.length - 1 ? 0 : currentIndex + 1);
    }
    
    const prevSlide = () => {
      setCurrentIndex(currentIndex === 0 ? images.length - 1 : currentIndex - 1);
    }
    
    return (
      <div className="slide">
        <button className="button-prev" onClick={prevSlide}><img src="" alt="" />{arrowLeft}</button>
        <img src={images[currentIndex]} alt="Slide" />
        <button className="button-next" onClick={nextSlide}>{arrowRight}</button>
      </div>
    );
  }
  
  export default SlideShow;
