import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";
import img from "../images/sphinx-bucegi-romania.jpg"
export default function Card(props) {

    var imgs;
    try {
        imgs = require("../images/" + props.item.id + ".jpg")

    }
    catch (err) {
        imgs = img;
    }

    const [dbList, setdbList] = useState([]);

    const handleClick = () => {

        Axios.post("http://localhost:3001/obiectiveC", {id: props.item.id}).then((response) =>{
            setdbList(response.data)
            console.log(response.data);
        });
    }

    return (
        <Link className="card"
         onClick={() => handleClick()} >

            <div className="img">

                <img src={imgs} alt="img" />

            </div>
            <div className="description">
                <h3>{props.item.nume}</h3>
                <p>{props.item.tip}</p>
                <p>{props.item.descriere}</p>

            </div>
        </Link>
    )
}
